mapper.select:
	Select 
			*		
		From 
		(
			select
				A.*, ROWNUM AS ROW_NUM
			from 
			(


	select 
		TABLE_NAME,COLUMN_NAME
	from GENSRC_TAB_COLS a 
	where 1=1
	
		and table_name = #{search_tableName}
	
	
		and column_name = #{search_columnName}
	






		                	${field.field} in 

			                	#{item}



			        		${field.field} = #{field.value}







		 order by ${sidx}  ${sord}  order by 1,2


	) A where 1=1 


					and (


			           		 ${filters.groupOp} 

								 is null
				    			REGEXP_LIKE(${item.field}, '^' || #{item.data} || '$','i')
				           		${item.field}  !=  #{item.data}
				           		${item.field}  <  #{item.data}
				           		${item.field}  <=  #{item.data}
				           		${item.field}  >  #{item.data}
				           		${item.field}  >=  #{item.data}
				           		REGEXP_LIKE(${item.field}, '^' || #{item.data},'i')
				           		 NOT REGEXP_LIKE(${item.field}, '^' || #{item.data},'i')


				           		 REGEXP_LIKE(${item.field}, #{item.data}|| '$','i')
				           		 NOT REGEXP_LIKE(${item.field}, #{item.data}|| '$','i')
				           		REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
				           		NOT REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')






			       			${filters.groupOp}

					        	 ${group.groupOp} 

									 is null	
					           		REGEXP_LIKE(${rule.field}, '^' || #{rule.data}|| '$','i')
					           		${rule.field}  !=  #{rule.data}
					           		${rule.field}  <  #{rule.data}
					           		${rule.field}  <=  #{rule.data}
					           		${rule.field}  >  #{rule.data}
					           		${rule.field}  >=  #{rule.data}
					           		REGEXP_LIKE(${rule.field}, '^' || #{rule.data},'i')
					           		 NOT REGEXP_LIKE(${rule.field}, '^' || #{rule.data},'i')


					           		 REGEXP_LIKE(${rule.field}, #{rule.data} || '$','i')
					           		 NOT REGEXP_LIKE(${rule.field}, #{rule.data} || '$','i')
					           		REGEXP_LIKE(${rule.field}, REGEXP_REPLACE(#{rule.data},' ','|'),'i')
					           		NOT REGEXP_LIKE(${rule.field}, REGEXP_REPLACE(#{rule.data},' ','|'),'i')





		       		 )


		) A
		where

	    	A.row_num >  to_number(#{rows}) * (to_number(#{page}) -1) 
	    	and A.row_num <= to_number(#{rows}) * to_number(#{page})

	select 
		ceil(count(*)/ #{rows} ) as total,
		#{page} as page ,
		count(*) records 
	from 
	(
		select a.* from (


	select 
		TABLE_NAME,COLUMN_NAME
	from GENSRC_TAB_COLS a 
	where 1=1
	
		and table_name = #{search_tableName}
	
	
		and column_name = #{search_columnName}
	






		                	${field.field} in 

			                	#{item}



			        		${field.field} = #{field.value}







		 order by ${sidx}  ${sord}  order by 1,2


	) a
	where 1=1 			


			and (


					 ${filters.groupOp} 

				    REGEXP_LIKE(${item.field}, '^' || #{item.data} || '$','i')
				           		${item.field}  !=  #{item.data}
				           		${item.field}  <  #{item.data}
				           		${item.field}  <=  #{item.data}
				           		${item.field}  >  #{item.data}
				           		${item.field}  >=  #{item.data}
				           		REGEXP_LIKE(${item.field}, '^' || #{item.data},'i')
				           		 NOT REGEXP_LIKE(${item.field}, '^' || #{item.data},'i')


				           		 REGEXP_LIKE(${item.field}, #{item.data} || '$','i')
				           		 NOT REGEXP_LIKE(${item.field}, #{item.data} || '$','i')
				           		REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
				           		NOT REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')






					${filters.groupOp}

						 ${group.groupOp} 

					           		REGEXP_LIKE(${rule.field}, '^' || #{rule.data} || '$','i')
					           		${rule.field}  !=  #{rule.data}
					           		${rule.field}  <  #{rule.data}
					           		${rule.field}  <=  #{rule.data}
					           		${rule.field}  >  #{rule.data}
					           		${rule.field}  >=  #{rule.data}
					           		REGEXP_LIKE(${rule.field}, '^' || #{rule.data},'i')
					           		 NOT REGEXP_LIKE(${rule.field}, '^' || #{rule.data},'i')


					           		 REGEXP_LIKE(${rule.field}, #{rule.data} || '$','i')
					           		 NOT REGEXP_LIKE(${rule.field}, #{rule.data} || '$','i')
					           		REGEXP_LIKE(${rule.field}, REGEXP_REPLACE(#{rule.data},' ','|'),'i')
					           		NOT REGEXP_LIKE(${rule.field}, REGEXP_REPLACE(#{rule.data},' ','|'),'i')





				)


	) a

select 
	TABLE_NAME,COLUMN_NAME
from GENSRC_TAB_COLS a 
where 1=1

	and table_name = #{search_tableName}


	and column_name = #{search_columnName}







	                	${field.field} in 

		                	#{item}



		        		${field.field} = #{field.value}







	 order by ${sidx}  ${sord}  order by 1,2

mapper.update:
declare
	cnt number;
begin
	insert into GENSRC_TAB_COLS(
		table_name,column_name
		,INS_DT
	)
	values (
		#{TABLE_NAME}
		,
		#{COLUMN_NAME}
		,sysdate
	);
end;

declare
	cnt number;
	begin
update GENSRC_TAB_COLS set
	MOD_Dt = sysdate,  
		 ${fieldName} = #{fieldValue}
	where 1=1
	
		
			
			 and ${item.field} = #{item.value}
			
		
		
		and 1=2
		
	
	;
end;

declare
	cnt number;
	begin
		delete from GENSRC_TAB_COLS
		where 1=1
		and table_name = #{detail.TABLE_NAME}
		and column_name = #{detail.COLUMN_NAME}
		;
end;
