mapper.select:
select ceil(count(*)/ #{rows} ) as total, #{page} as page , count(*) records from ( select a.* from ( select d.YYYYMM, d.TEST_BOARD, d.SAMPLE, d.FIRMWARE, d.SCRIPT_NAME, d.SAMPLE_NUMBER, d.SCRIPT, d.SEQ, d.TEST_TIME, nvl(m.STATUS,'Not Yet') as STATUS, d.STATUS_DETAIL, d.DURATION,
m.SCRIPT_NAME as SCRIPT_NAME_META, sm.CONVERT_SCRIPT, m.CATEGORY, m.TEST_ITEM, m.TIME TEST_TIME_MASTER, m.CUSTOMER_ITEM, m.NEED_VENDOR_CMD, m.NEED_POWER_CYCLE, m.EMMC_VER, m.TARGET_DEVICE, m.TARGET_PARTITION, m.CATEGORY1, m.CATEGORY2, m.CATEGORY3, m.CATEGORY4, m.CATEGORY5, m.WRITE_MODE, m.READ_MODE, substr(m.DESCRIPTION,1,20) || ' ...' as DESCRIPTION_SIMPLE, m.DESCRIPTION, substr(m.ARGUMENT,1,20) || ' ...' as ARGUMENT_SIMPLE, m.ARGUMENT, m.PLATFORM, m.FUNCTION_NAME, m.JIRA
from CORONA_EMMC_F_S_MAP m left outer join CORONA_EMMC_TEST_DETAIL_LAST d on m.firmware = d.firmware and m.sample = d.sample and m.script_name = d.script left outer join CORONA_EMMC_SCRIPT_MASTER sm on sm.script_name = m.script_name where 1=1 and m.sample = #{sample} and m.FIRMWARE = #{firmware} and m.CATEGORY = #{category} and m.test_item = #{testItem} and m.script_name = #{script_name} and m.STATUS = 'PASS' and m.STATUS = 'FAIL' and m.STATUS is null order by m.SAMPLE,m.FIRMWARE, m.TEST_ITEM,d.YYYYMM,d.TEST_BOARD, d.SAMPLE, d.FIRMWARE, d.SCRIPT_NAME, d.SAMPLE_NUMBER, d.SCRIPT ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
)

select
	SCRIPTSET
from CORONA_EMMC_SCRIPTSET_MAP_F
where firmware = #{firmware}
and sample= #{sample}

mapper.update:
    	declare
	    	cnt number;
	    begin

	    		select count(*) into cnt from CORONA_EMMC_TESTITEM_MASTER
	    		where FIRMWARE = #{origindata.FIRMWARE}
                and  SAMPLE = #{origindata.SAMPLE}
                and  CATEGORY = #{origindata.CATEGORY} 
                and  TEST_ITEM = #{origindata.TEST_ITEM};

	    		if cnt = 1 then
	                update CORONA_EMMC_TESTITEM_MASTER set
	                	WEIGHT = #{WEIGHT}, 
	                	COMPUTE_CRETERIA = #{COMPUTE_CRETERIA}, 
	                	COMPUTE_VALUE = #{COMPUTE_VALUE}, 
	  					TEST_STATE = #{testState},
	                	MOD_DT = sysdate
	                where FIRMWARE = #{origindata.FIRMWARE}
	                and  SAMPLE = #{origindata.SAMPLE}
	                and  CATEGORY = #{origindata.CATEGORY} 
	                and  TEST_ITEM = #{origindata.TEST_ITEM};
	          	else
	          		insert into CORONA_EMMC_TESTITEM_MASTER(FIRMWARE, SAMPLE, CATEGORY, TEST_ITEM, WEIGHT, COMPUTE_CRETERIA, COMPUTE_VALUE, TEST_STATE, INS_DT)
	          		values (#{origindata.FIRMWARE}, #{origindata.SAMPLE}, #{origindata.CATEGORY}, #{origindata.TEST_ITEM}, #{WEIGHT}, #{COMPUTE_CRETERIA}, #{COMPUTE_VALUE}, #{testState},sysdate);

	          	end if;


	    end;

mapper.select:
Select * From ( select A.*, ROWNUM AS ROW_NUM from ( SELECT CATEGORY, SCRIPT_NAME, CONVERT_SCRIPT, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, substr(DESCRIPTION,1,20) || ' ...' as DESCRIPTION_SIMPLE, DESCRIPTION, substr(ARGUMENT,1,20) || ' ...' as ARGUMENT_SIMPLE, ARGUMENT, PLATFORM, FUNCTION_NAME, INS_DT, MOD_DT from CORONA_EMMC_SCRIPT_MASTER a where 1=1
and a.category in
#{item}
and a.test_item in
#{item}
--order by category,test_item, script_name order by category ,script_name ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
) A where
A.row_num > to_number(#{rows}) * (to_number(#{page}) -1) and A.row_num <= to_number(#{rows}) * to_number(#{page})

select ceil(count(*)/ #{rows} ) as total, #{page} as page , count(*) records from ( select a.* from ( SELECT CATEGORY, SCRIPT_NAME, CONVERT_SCRIPT, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, substr(DESCRIPTION,1,20) || ' ...' as DESCRIPTION_SIMPLE, DESCRIPTION, substr(ARGUMENT,1,20) || ' ...' as ARGUMENT_SIMPLE, ARGUMENT, PLATFORM, FUNCTION_NAME, INS_DT, MOD_DT from CORONA_EMMC_SCRIPT_MASTER a where 1=1
and a.category in
#{item}
and a.test_item in
#{item}
--order by category,test_item, script_name order by category ,script_name ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
)

Select * From ( select A.*, ROWNUM AS ROW_NUM from ( SELECT m.SAMPLE, m.FIRMWARE, m.SCRIPT_NAME, sm.CONVERT_SCRIPT, m.CATEGORY, m.TEST_ITEM, m.TIME, m.CUSTOMER_ITEM, m.NEED_VENDOR_CMD, m.NEED_POWER_CYCLE, m.EMMC_VER, m.TARGET_DEVICE, m.TARGET_PARTITION, m.CATEGORY1, m.CATEGORY2, m.CATEGORY3, m.CATEGORY4, m.CATEGORY5, m.WRITE_MODE, m.READ_MODE, substr(m.DESCRIPTION,1,20) || ' ...' as DESCRIPTION_SIMPLE, m.DESCRIPTION, substr(m.ARGUMENT,1,20) || ' ...' as ARGUMENT_SIMPLE, m.ARGUMENT, m.PLATFORM, m.FUNCTION_NAME, m.INS_DT, m.MOD_DT from CORONA_EMMC_F_S_MAP m left outer join CORONA_EMMC_SCRIPT_MASTER sm on sm.script_name = m.script_name where m.sample = #{sample} and m.FIRMWARE = #{firmware} order by m.category, m.test_item, m.script_name ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
) A where
A.row_num > to_number(#{rows}) * (to_number(#{page}) -1) and A.row_num <= to_number(#{rows}) * to_number(#{page})

select ceil(count(*)/ #{rows} ) as total, #{page} as page , count(*) records from ( select a.* from ( SELECT m.SAMPLE, m.FIRMWARE, m.SCRIPT_NAME, sm.CONVERT_SCRIPT, m.CATEGORY, m.TEST_ITEM, m.TIME, m.CUSTOMER_ITEM, m.NEED_VENDOR_CMD, m.NEED_POWER_CYCLE, m.EMMC_VER, m.TARGET_DEVICE, m.TARGET_PARTITION, m.CATEGORY1, m.CATEGORY2, m.CATEGORY3, m.CATEGORY4, m.CATEGORY5, m.WRITE_MODE, m.READ_MODE, substr(m.DESCRIPTION,1,20) || ' ...' as DESCRIPTION_SIMPLE, m.DESCRIPTION, substr(m.ARGUMENT,1,20) || ' ...' as ARGUMENT_SIMPLE, m.ARGUMENT, m.PLATFORM, m.FUNCTION_NAME, m.INS_DT, m.MOD_DT from CORONA_EMMC_F_S_MAP m left outer join CORONA_EMMC_SCRIPT_MASTER sm on sm.script_name = m.script_name where m.sample = #{sample} and m.FIRMWARE = #{firmware} order by m.category, m.test_item, m.script_name ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
)

mapper.update:
    	declare
	    	cnt number;
	    	v_script_name varchar2(2000);
	    	v_convert_script varchar2(2000);
	    	v_status varchar2(10);
	    begin

	    		select count(*) into cnt from CORONA_EMMC_SCRIPT_MASTER
	    		where 1=1



			       			and ${item.field} = #{item.value}



			   			and 1=2


	    		;

	    		if cnt = 1 then
	    			update CORONA_EMMC_SCRIPT_MASTER set
	              	${fieldName} = #{fieldValue},	  
	              	MOD_DT = sysdate
	              	where 1=1



				       			and ${item.field} = #{item.value}



				   			and 1=2


	          		;


		          		update CORONA_EMMC_SCRIPTSET_MAP set
		              	${fieldName} = #{fieldValue},	  
		              	MOD_DT = sysdate
		              	where 1=1



					       			and ${item.field} = #{item.value}



					   			and 1=2


		          		;


		          		update CORONA_EMMC_F_S_MAP set
		              	${fieldName} = #{fieldValue},	  
		              	MOD_DT = sysdate
		              	where 1=1



					       			and ${item.field} = #{item.value}



					   			and 1=2


		          		;



			        	update CORONA_EMMC_TEST_DETAIL set
	          			CONVERT_SCRIPT = #{fieldValue},
	          			MOD_DT = sysdate
	          			where CONVERT_SCRIPT = #{fieldValueOrigin};



	          			v_script_name := #{fieldValue};   

	          			--// update CORONA_EMMC_SCRIPTSET_MAP
	          			 update CORONA_EMMC_SCRIPTSET_MAP set
	          			 SCRIPT_NAME = #{fieldValue},
	          			 DESCRIPTION = DESCRIPTION || case when DESCRIPTION is not null then  chr(10) end  || to_char(sysdate,'YYYY-MM-DD HH24:MI') || ': ' || #{fieldValueOrigin} || ' ==> ' || #{fieldValue} || ' ( ${userId} )'  ,
	          			 MOD_DT = sysdate
	          			 where SCRIPT_NAME = #{fieldValueOrigin};

	          			--// update_firmware_table	          			
	          			update CORONA_EMMC_F_S_MAP set
	          			SCRIPT_NAME = #{fieldValue},
	          			DESCRIPTION = DESCRIPTION || case when DESCRIPTION is not null then  chr(10) end  || to_char(sysdate,'YYYY-MM-DD HH24:MI') || ': ' || #{fieldValueOrigin} || ' ==> ' || #{fieldValue} || ' ( ${userId} )'  ,
	          			MOD_DT = sysdate
	          			where SCRIPT_NAME = #{fieldValueOrigin};


				   		--// update_log_table
	          			update CORONA_EMMC_TEST_DETAIL set
	          			SCRIPT = #{fieldValue},
	          			MOD_DT = sysdate
	          			where Script = #{fieldValueOrigin};

	          			--// update_log_table
	          			update CORONA_EMMC_TEST_DETAIL_LAST set
	          			SCRIPT = #{fieldValue},
	          			MOD_DT = sysdate
	          			where Script = #{fieldValueOrigin};

	          			--// update_comment
	          			update CORONA_EMMC_SCRIPT_MASTER set
	          			DESCRIPTION = DESCRIPTION || case when DESCRIPTION is not null then  chr(10) end  || to_char(sysdate,'YYYY-MM-DD HH24:MI') || ': ' || #{fieldValueOrigin} || ' ==> ' || #{fieldValue} || ' ( ${userId} )'  ,
	          			MOD_DT = sysdate
	          			where SCRIPT_NAME = #{fieldValue};


	          			--//  Status
		          		for rec_scriptset in (
		          			select SCRIPTSET from CORONA_EMMC_SCRIPT_SETS
		          		) loop 
		          			for rec_firmwares in (
		          				select  FIRMWARE, SAMPLE, SCRIPTSET  from CORONA_EMMC_SCRIPTSET_MAP_F
		          				where SCRIPTSET = rec_scriptset.SCRIPTSET
		          			) loop
		          				--//Start. status update
								v_status := null;
								for rec_detail in (
									select
										STATUS
									from CORONA_EMMC_TEST_DETAIL a
									where a.sample= rec_firmwares.sample
									and a.firmware = rec_firmwares.firmware
									and a.script = v_script_name
									order by YYYYMM
								) loop
									v_status := rec_detail.STATUS;
									if rec_detail.STATUS = 'FAIL' then 
										exit;
									end if;
								end loop;

								UPDATE CORONA_EMMC_F_S_MAP SET						
									status = v_status
									,DESCRIPTION = DESCRIPTION || chr(10) || to_char(sysdate,'YYYY-MM-DD HH24:MI') || ': ' || STATUS || ' ==> ' || v_status || ' (management) '
									,MOD_DT = sysdate
								where firmware = rec_firmwares.firmware
								and sample = rec_firmwares.sample
								and script_name =  v_script_name;
								--// End. status update



		          			end loop;
		          			--// end rec_firmwares

		          		end loop;
		          		--// end rec_scriptset
		          		--// End Status  		






	        	end if;


	    end;

    	declare
	    	cnt number;
	    	v_status varchar2(100);
	    begin

    		insert into CORONA_EMMC_SCRIPT_MASTER (SCRIPT_NAME, CONVERT_SCRIPT,  CATEGORY, TEST_ITEM,INS_DT)
            values (#{SCRIPT_NAME}, #{CONVERT_SCRIPT} ,#{CATEGORY},#{TEST_ITEM}, sysdate) ;

            select count(*) into cnt from CORONA_EMMC_SCRIPTSET_MAP
            where SCRIPTSET = 'Normal'
            and SCRIPT_NAME = #{SCRIPT_NAME};

            if cnt = 0 then
            	insert into CORONA_EMMC_SCRIPTSET_MAP (SCRIPTSET, SCRIPT_NAME, CATEGORY, TEST_ITEM,INS_DT)
            	values ('Normal', #{SCRIPT_NAME},#{CATEGORY},#{TEST_ITEM}, sysdate);
            end if;

	        For rec_firmware in (
				select FIRMWARE, SAMPLE,SCRIPTSET from CORONA_EMMC_SCRIPTSET_MAP_F
				where 1=1
				and SCRIPTSET = 'Normal'
			)
			Loop
				-- DELETE	
				delete from CORONA_EMMC_F_S_MAP 
				where FIRMWARE = rec_firmware.firmware
				and SAMPLE = rec_firmware.sample
				and SCRIPT_NAME = #{SCRIPT_NAME};
				-- INSERT
				insert into CORONA_EMMC_F_S_MAP (FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM,INS_DT)
				select
					rec_firmware.firmware,
					rec_firmware.sample,
					SCRIPT_NAME, 
					CATEGORY,
					TEST_ITEM,
					sysdate
				from CORONA_EMMC_SCRIPTSET_MAP
				where SCRIPTSET = 'Normal'
				and script_name = #{SCRIPT_NAME};


				--// 04 STATUS UPDATE
				for rec_script in (
					select * from CORONA_EMMC_F_S_MAP 
					where firmware = rec_firmware.firmware and sample = rec_firmware.sample
					and script_name = #{SCRIPT_NAME}
				) loop 
					v_status := null;
					for rec_detail in (
						select
							STATUS
						from CORONA_EMMC_TEST_DETAIL a
						where a.sample= rec_script.sample
						and a.firmware = rec_script.firmware
						and a.script = rec_script.script_name
						order by YYYYMM
					) loop
						v_status := rec_detail.STATUS;
						if rec_detail.STATUS = 'FAIL' then 
							exit;
						end if;
					end loop;

					UPDATE CORONA_EMMC_F_S_MAP SET						
						status = v_status,
						DESCRIPTION = DESCRIPTION || chr(10) || to_char(sysdate,'YYYY-MM-DD HH24:MI') || ': ' || STATUS || ' ==> ' || v_status || ' (management) ',
						MOD_DT = sysdate
					where firmware = rec_script.firmware
					and sample = rec_script.sample
					and script_name = rec_script.script_name;
				End Loop;
				--// END 04 STATUS UPDATE	

			End Loop;

	    end;

    	declare
	    	cnt number;
	    begin

	    		delete from CORONA_EMMC_SCRIPT_MASTER
	    		where 1=1
	    		and SCRIPT_NAME = #{detail.SCRIPT_NAME};

	    		--// 01. delete script set
	    		delete from CORONA_EMMC_SCRIPTSET_MAP
	    		where 1=1
	    		and SCRIPT_NAME = #{detail.SCRIPT_NAME};


	    		--// 02. delete firmware
	    		delete from CORONA_EMMC_F_S_MAP
	    		where 1=1
	    		and SCRIPT_NAME = #{detail.SCRIPT_NAME};
	    end;

    	declare
	    	cnt number;
	    begin

	    		select count(*) into cnt from CORONA_EMMC_F_S_MAP
	    		where 1=1



			       			and ${item.field} = #{item.value}



			   			and 1=2


	    		;

	    		if cnt = 1 then
	              	update CORONA_EMMC_F_S_MAP set
	              	${fieldName} = #{fieldValue},	  
	              	MOD_DT = sysdate
	              	where 1=1



				       			and ${item.field} = #{item.value}



				   			and 1=2


	          		;
	        end if;


	    end;

    	declare
	    	cnt number;
	    begin

	    		insert into CORONA_EMMC_F_S_MAP (FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM,INS_DT)
                values (#{FIRMWARE},#{SAMPLE},#{SCRIPT_NAME},#{CATEGORY},#{TEST_ITEM}, sysdate) ;

	    end;

    	declare
	    	cnt number;
	    begin
	    		for rec in (
	    			with tbl_list as (
	    				select 'xxx' as firmware , 'xxx' as sample , 'xxx' as script_name  from dual



			       			select #{item.FIRMWARE} as firmware , #{item.SAMPLE} as sample , #{item.SCRIPT_NAME} as script_name  from dual



	    			)

	    			select * from tbl_list

	    		) loop 
	    			delete from CORONA_EMMC_F_S_MAP 
	    			where firmware = rec.firmware
	    			and sample = rec.sample
	    			and script_name = rec.script_name;

	    		end loop;
	    end;

    	declare
	    	cnt number;
	    begin
	    		for rec in (
	    			with tbl_list as (
	    				select 'xxx' as firmware , 'xxx' as sample , 'xxx' as script_name  from dual
	    				union all
			       		select #{detail.FIRMWARE} as firmware , #{detail.SAMPLE} as sample , #{detail.SCRIPT_NAME} as script_name  from dual
	    			)

	    			select * from tbl_list

	    		) loop 
	    			delete from CORONA_EMMC_F_S_MAP 
	    			where firmware = rec.firmware
	    			and sample = rec.sample
	    			and script_name = rec.script_name;

	    		end loop;
	    end;

    	DECLARE
	    	cnt number;
	    	v_history_no varchar2(100) := #{history_no};
	    BEGIN
	    	INSERT INTO CORONA_EMMC_HISTORY_NO (HISTORY_NO, INS_DT) VALUES (v_history_no , sysdate );
	    	insert into CORONA_EMMC_MAP_HISTORY (HISTORY_NO, FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, DESCRIPTION, ARGUMENT, PLATFORM, FUNCTION_NAME, INS_DT, MOD_DT)
	    		select 
	    			v_history_no,
	    			FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, DESCRIPTION, ARGUMENT, PLATFORM, FUNCTION_NAME, INS_DT, MOD_DT
	    		from CORONA_EMMC_F_S_MAP
	    		where 1=1
	    		and sample = #{searchJson.target.sample} ;
	    End;

    	DECLARE
	    	cnt number;
	    	v_history_no varchar2(100) := #{history_no};
	    BEGIN
	    	INSERT INTO CORONA_EMMC_HISTORY_NO (HISTORY_NO, INS_DT) VALUES (v_history_no , sysdate );
	    	insert into CORONA_EMMC_MAP_HISTORY (HISTORY_NO, FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, DESCRIPTION, ARGUMENT, PLATFORM, FUNCTION_NAME, INS_DT, MOD_DT)
	    		select 
	    			v_history_no,
	    			FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, DESCRIPTION, ARGUMENT, PLATFORM, FUNCTION_NAME, INS_DT, MOD_DT
	    		from CORONA_EMMC_F_S_MAP
	    		where 1=1
	    		and sample = #{searchJson.target.sample}
	    		and firmware = #{searchJson.target.firmware}
	    		 ;
	    End;

    	DECLARE
	    	cnt number;
	    	v_history_no varchar2(100) := #{history_no};
	    BEGIN
	    	INSERT INTO CORONA_EMMC_HISTORY_NO (HISTORY_NO, INS_DT) VALUES (v_history_no , sysdate );
	    	insert into CORONA_EMMC_MAP_HISTORY (HISTORY_NO, FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, DESCRIPTION, ARGUMENT, PLATFORM, FUNCTION_NAME, INS_DT, MOD_DT)
	    		select 
	    			v_history_no,
	    			FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, DESCRIPTION, ARGUMENT, PLATFORM, FUNCTION_NAME, INS_DT, MOD_DT
	    		from CORONA_EMMC_F_S_MAP
	    		where 1=1
	    		and sample = #{searchJson.target.sample}
	    		and firmware = #{searchJson.target.firmware}
	    		 ;
	    End;

    	DECLARE
	    	cnt number;
	    	v_history_no varchar2(100) := #{history_no};
	    BEGIN

	    	--// Revision Start	    		
	    	For rec_firmware in (
	    		select distinct firmware from CORONA_EMMC_FIRMWARE_MASTER where sample = #{searchJson.target.sample}
	    	) Loop

	    		--// second Loop
	    		FOR rec in (
	    			select * from CORONA_EMMC_SCRIPT_MASTER
					where 1=1
					and script_name = #{detail.SCRIPT_NAME}    			
	    		) LOOP 


					select count(*) into cnt from CORONA_EMMC_F_S_MAP
					where firmware = rec_firmware.firmware
					and sample = #{searchJson.target.sample}
					and script_name = rec.SCRIPT_NAME;

					IF cnt = 0 THEN
						insert into CORONA_EMMC_F_S_MAP (FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, DESCRIPTION, ARGUMENT,PLATFORM, FUNCTION_NAME, INS_DT)
						values (
							rec_firmware.firmware , #{searchJson.target.sample}
							, rec.SCRIPT_NAME, rec.CATEGORY, rec.TEST_ITEM, rec.TIME, rec.CUSTOMER_ITEM, rec.NEED_VENDOR_CMD, rec.NEED_POWER_CYCLE, rec.EMMC_VER, rec.TARGET_DEVICE, rec.TARGET_PARTITION, rec.CATEGORY1,rec. CATEGORY2, rec.CATEGORY3, rec.CATEGORY4, rec.CATEGORY5, rec.WRITE_MODE, rec.READ_MODE, rec.DESCRIPTION, rec.ARGUMENT, rec.PLATFORM, rec.FUNCTION_NAME
							, sysdate
						);
					ELSE

						UPDATE CORONA_EMMC_F_S_MAP SET
							CATEGORY = rec.CATEGORY,
							TEST_ITEM = rec.TEST_ITEM,
							TIME = rec.TIME,
							CUSTOMER_ITEM = rec.CUSTOMER_ITEM,
							NEED_VENDOR_CMD = rec.NEED_VENDOR_CMD,
							NEED_POWER_CYCLE = rec.NEED_POWER_CYCLE,
							EMMC_VER = rec.EMMC_VER,
							TARGET_DEVICE = rec.TARGET_DEVICE,
							TARGET_PARTITION = rec.TARGET_PARTITION,
							CATEGORY1 = rec.CATEGORY1,
							CATEGORY2 = rec.CATEGORY2,
							CATEGORY3 = rec.CATEGORY3,
							CATEGORY4 = rec.CATEGORY4,
							CATEGORY5 = rec.CATEGORY5,
							WRITE_MODE = rec.WRITE_MODE,
							READ_MODE = rec.READ_MODE,
							DESCRIPTION = rec.DESCRIPTION,
							ARGUMENT = rec.ARGUMENT,
							PLATFORM = rec.PLATFORM,
							FUNCTION_NAME =  rec.FUNCTION_NAME,
							MOD_DT = sysdate
						WHERE firmware = rec_firmware.firmware
						AND sample = #{searchJson.target.sample}
						AND script_name = rec.script_name; 


					END IF;

				End Loop;

	    	End Loop;





	    END;

    	DECLARE
	    	cnt number;
	    	v_history_no varchar2(100) := #{history_no};
	    BEGIN

	    	--// Revision Start	    		
	    	For rec_firmware in (
	    		select distinct firmware from CORONA_EMMC_FIRMWARE_MASTER where sample = #{searchJson.target.sample} and firmware = #{searchJson.target.firmware} 
	    	) Loop

	    		--// second Loop
	    		FOR rec in (
	    			select * from CORONA_EMMC_SCRIPT_MASTER
					where 1=1
					and script_name = #{detail.SCRIPT_NAME}    			
	    		) LOOP 


					select count(*) into cnt from CORONA_EMMC_F_S_MAP
					where firmware = rec_firmware.firmware
					and sample = #{searchJson.target.sample}
					and script_name = rec.SCRIPT_NAME;

					IF cnt = 0 THEN
						insert into CORONA_EMMC_F_S_MAP (FIRMWARE, SAMPLE, SCRIPT_NAME, CATEGORY, TEST_ITEM, TIME, CUSTOMER_ITEM, NEED_VENDOR_CMD, NEED_POWER_CYCLE, EMMC_VER, TARGET_DEVICE, TARGET_PARTITION, CATEGORY1, CATEGORY2, CATEGORY3, CATEGORY4, CATEGORY5, WRITE_MODE, READ_MODE, DESCRIPTION, ARGUMENT,PLATFORM, FUNCTION_NAME, INS_DT)
						values (
							rec_firmware.firmware , #{searchJson.target.sample}
							, rec.SCRIPT_NAME, rec.CATEGORY, rec.TEST_ITEM, rec.TIME, rec.CUSTOMER_ITEM, rec.NEED_VENDOR_CMD, rec.NEED_POWER_CYCLE, rec.EMMC_VER, rec.TARGET_DEVICE, rec.TARGET_PARTITION, rec.CATEGORY1,rec. CATEGORY2, rec.CATEGORY3, rec.CATEGORY4, rec.CATEGORY5, rec.WRITE_MODE, rec.READ_MODE, rec.DESCRIPTION, rec.ARGUMENT, rec.PLATFORM, rec.FUNCTION_NAME
							, sysdate
						);
					ELSE

						UPDATE CORONA_EMMC_F_S_MAP SET
							CATEGORY = rec.CATEGORY,
							TEST_ITEM = rec.TEST_ITEM,
							TIME = rec.TIME,
							CUSTOMER_ITEM = rec.CUSTOMER_ITEM,
							NEED_VENDOR_CMD = rec.NEED_VENDOR_CMD,
							NEED_POWER_CYCLE = rec.NEED_POWER_CYCLE,
							EMMC_VER = rec.EMMC_VER,
							TARGET_DEVICE = rec.TARGET_DEVICE,
							TARGET_PARTITION = rec.TARGET_PARTITION,
							CATEGORY1 = rec.CATEGORY1,
							CATEGORY2 = rec.CATEGORY2,
							CATEGORY3 = rec.CATEGORY3,
							CATEGORY4 = rec.CATEGORY4,
							CATEGORY5 = rec.CATEGORY5,
							WRITE_MODE = rec.WRITE_MODE,
							READ_MODE = rec.READ_MODE,
							DESCRIPTION = rec.DESCRIPTION,
							ARGUMENT = rec.ARGUMENT,
							PLATFORM = rec.PLATFORM,
							FUNCTION_NAME =  rec.FUNCTION_NAME,
							MOD_DT = sysdate
						WHERE firmware = rec_firmware.firmware
						AND sample = #{searchJson.target.sample}
						AND script_name = rec.script_name; 


					END IF;

				End Loop;

	    	End Loop;





	    END;

    	DECLARE
	    	cnt number;
	    	v_history_no varchar2(100) := #{history_no};
	    BEGIN

	    	--// Revision Start	    		
	    	For rec_firmware in (
	    		select distinct firmware from CORONA_EMMC_FIRMWARE_MASTER where sample = #{searchJson.target.sample} and firmware = #{searchJson.target.firmware} 
	    	) Loop

	    		--// second Loop
	    		FOR rec in (
	    			select * from CORONA_EMMC_F_S_MAP
					where 1=1
					and firmware = #{detail.FIRMWARE}
					and sample = #{detail.SAMPLE}
