						AND sample = #{searchJson.target.sample}
						AND script_name = rec.script_name; 


					END IF;

				End Loop;

	    	End Loop;





	    END;

    	declare
	    	cnt number;
	    	vStatus varchar2(10) := #{STATUS};
	    	vStatus1 varchar2(10) ;
	    begin

	    		select count(*) into cnt from CORONA_EMMC_F_S_MAP
	    		where FIRMWARE = #{origindata.FIRMWARE}
                and  SAMPLE = #{origindata.SAMPLE}
                and  SCRIPT_NAME = #{origindata.SCRIPT_NAME_META} ;

	    		if cnt = 1 then
	    			if vStatus = 'Not Yet' then 
	    				vStatus1 := '';
	    			else
	    				vStatus1 := vStatus;
	    			end if;

	                update CORONA_EMMC_F_S_MAP set
	                	STATUS = vStatus1, 
	                	STATUS_CUSTOM = 'Y', 
	                	JIRA = #{JIRA},
	                	DESCRIPTION = DESCRIPTION || chr(10) || to_char(sysdate,'YYYY-MM-DD HH24:MI') || ': ' || STATUS || ' ==> ' || #{STATUS} || ' ( ${userId} )'  ,

	                	MOD_DT = sysdate
	                where FIRMWARE = #{origindata.FIRMWARE}
	                and  SAMPLE = #{origindata.SAMPLE}
	                and  SCRIPT_NAME = #{origindata.SCRIPT_NAME_META} ;

	          	end if;


	    end;

    	declare
	    	cnt number;
	    	vStatus varchar2(10) := #{STATUS};
	    	vStatus1 varchar2(10) ;
	    begin

	    		select count(*) into cnt from CORONA_EMMC_TEST_DETAIL
	    		where 1=1
                and  SAMPLE = #{origindata.SAMPLE}
                and FIRMWARE = #{origindata.FIRMWARE}
                and SCRIPT = #{origindata.SCRIPT}
                and YYYYMM = #{origindata.YYYYMM}
                and TEST_BOARD = #{origindata.TEST_BOARD}
                and  SCRIPT_NAME = #{origindata.SCRIPT_NAME}
                and  SAMPLE_NUMBER = #{origindata.SAMPLE_NUMBER};                

	    		if cnt = 1 then
	    			if vStatus = 'Not Yet' then 
	    				vStatus1 := '';
	    			else
	    				vStatus1 := vStatus;
	    			end if;

	                update CORONA_EMMC_TEST_DETAIL set
	                	STATUS = vStatus1, 
	                	MOD_DT = sysdate
	                where  SAMPLE = #{origindata.SAMPLE}
	                and FIRMWARE = #{origindata.FIRMWARE}
	                and SCRIPT = #{origindata.SCRIPT}
	                and YYYYMM = #{origindata.YYYYMM}
	                and TEST_BOARD = #{origindata.TEST_BOARD}
	                and  SCRIPT_NAME = #{origindata.SCRIPT_NAME}
	                and  SAMPLE_NUMBER = #{origindata.SAMPLE_NUMBER};                

	          	end if;


	    end;

    	declare
			v_status varchar2(10);

		begin	

			-- start firmware status
			-- 01. firmware script list
			for rec in (
				select
					FIRMWARE,SAMPLE, SCRIPT_NAME
				from CORONA_EMMC_F_S_MAP
				where sample = #{sample}
				and FIRMWARE = #{firmware}	 
				--order by FIRMWARE, SAMPLE, SCRIPT_NAME
			) loop
			-- 02. detail log list , determine status
				v_status := '';
				for rec1 in (
					select
						STATUS
					from CORONA_EMMC_TEST_DETAIL a
					where a.sample= rec.sample
					and a.firmware = rec.firmware
					and a.script = rec.script_name
					order by YYYYMM
				) loop
					v_status := rec1.STATUS;
					if rec1.STATUS = 'FAIL' then 
						exit;
					end if;
				end loop;

				update CORONA_EMMC_F_S_MAP set
					status = v_status,
					DESCRIPTION = DESCRIPTION || chr(10) || to_char(sysdate,'YYYY-MM-DD HH24:MI') || ': ' || STATUS || ' ==> ' || v_status || ' (management) ',
					MOD_DT = sysdate
				where firmware = rec.firmware
				and sample= rec.sample
				and script_name = rec.script_name;

			end loop;


		end;

    	declare
	    	cnt number;
	    	vUseyn varchar2(10) := #{USE_YN};
	    begin

	    		select count(*) into cnt from CORONA_EMMC_PROJECT
	    		where PROJECT = #{origindata.PROJECT} ;

	    		if cnt = 0 then
	    			insert into CORONA_EMMC_PROJECT(PROJECT, USE_YN, SCRIPTSET,  INS_DT)
	    			values (#{origindata.PROJECT},#{USE_YN}, #{SCRIPTSET}, sysdate);
	    		else	    		
	                update CORONA_EMMC_PROJECT set
	                	USE_YN = vUseyn, 
	                	SCRIPTSET = #{SCRIPTSET} ,
	                	MOD_DT = sysdate
	               where PROJECT = #{origindata.PROJECT} ;

	          	end if;


	    end;

mapper.select:
select a.SAMPLE, a.FIRMWARE, b.FW_VERSION
from CORONA_EMMC_FIRMWARE_MASTER A join SOLUTIONPMS.FW_RELEASE@Pms_dsbd_inf b on REGEXP_REPLACE(a.FIRMWARE,'-([0-9]+)$','') = b.fw_version and REGEXP_REPLACE(a.FIRMWARE,'^(.+)-([0-9]+)$','\2') = b.BUILD_NO and (SELECT CASE WHEN CODE_ID = 'SPM_00005011' THEN 'eMMC' ELSE 'SSD' END FROM SOLUTIONPMS.PJT_BASE_INFO@Pms_dsbd_inf WHERE CODE_NAME = 'Application' AND PJT_ID = B.PJT_ID) != 'SSD' and SOLUTIONPMS.FN_CODE_VALUE_ID@Pms_dsbd_inf(b.VERIFY_RESULT_CD) = 'Pass' and SOLUTIONPMS.FN_CODE_VALUE_ID@Pms_dsbd_inf(b.RELEASE_STATUS_CD) != 'Cancelled' and NVL(b.OVT_YN, 'N') = 'N' where 1=1
and a.sample = #{project}

Select * From ( select A.*, ROWNUM AS ROW_NUM from ( select a.PROJECT, a.FIRMWARE, a.FIRMWARE FIRMWARE_NM, a.NAND_TYPE, a.REMARK, to_char(a.RELEASE_DATE,'YYYY-MM-DD') as RELEASE_DATE, a.SCRIPTSET, a.INS_DT, a.MOD_DT from SSD_SM_FIRMWARE a where 1=1
and a.PROJECT = #{project}
order by a.project, RELEASE_DATE desc ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
) A where
A.row_num > to_number(#{rows}) * (to_number(#{page}) -1) and A.row_num <= to_number(#{rows}) * to_number(#{page})

select ceil(count(*)/ #{rows} ) as total, #{page} as page , count(*) records from ( select a.* from ( select a.PROJECT, a.FIRMWARE, a.FIRMWARE FIRMWARE_NM, a.NAND_TYPE, a.REMARK, to_char(a.RELEASE_DATE,'YYYY-MM-DD') as RELEASE_DATE, a.SCRIPTSET, a.INS_DT, a.MOD_DT from SSD_SM_FIRMWARE a where 1=1
and a.PROJECT = #{project}
order by a.project, RELEASE_DATE desc ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
)

mapper.update:
    	declare
	    	cnt number;
	    begin

	    		insert into SSD_SM_SCRIPTSET (SCRIPTSET, INS_DT)
                values (#{SCRIPTSET}, sysdate) ;


				delete from SSD_SM_SCRIPTSET_MAP where SCRIPTSET = #{SCRIPTSET};

				insert into SSD_SM_SCRIPTSET_MAP (SCRIPTSET, SCRIPT_NAME, INS_DT)
				select 
					#{SCRIPTSET}, SCRIPT_NAME, sysdate
				from SSD_SM_SCRIPTSET_MAP
				where scriptset = #{copyfrom};



	    end;

    	declare
	    	cnt number;
	    begin

	    		if #{origindata.SCRIPTSET} != 'Normal' Then
		    		delete from SSD_SM_SCRIPTSET
		    		where SCRIPTSET = #{origindata.SCRIPTSET} ;

		    		delete from SSD_SM_SCRIPTSET_MAP
		    		where SCRIPTSET = #{origindata.SCRIPTSET} ;


		    	End if;


	    end;

    	declare
	    	cnt number;
	    begin
	    	if #{origindata.SCRIPTSET} != 'Normal' Then
	    		update SSD_SM_SCRIPTSET set 
	    		SCRIPTSET = #{SCRIPTSET},
	    		mod_dt = sysdate
	    		where SCRIPTSET = #{origindata.SCRIPTSET} ;


	    		update SSD_SM_SCRIPTSET_MAP set
	    		SCRIPTSET = #{SCRIPTSET}
	    		where 	SCRIPTSET = #{origindata.SCRIPTSET} ;

	    		update SSD_SM_SCRIPTSET_MAP_F set
	    		SCRIPTSET = #{SCRIPTSET}
	    		where 	SCRIPTSET = #{origindata.SCRIPTSET} ;


               end if;

	    end;

    	declare
	    	cnt number;
	    begin

	    		insert into SSD_SM_FIRMWARE (PROJECT, FIRMWARE, NAND_TYPE, REMARK, RELEASE_DATE, SCRIPTSET,INS_DT)
                values (#{PROJECT},#{FIRMWARE},#{NAND_TYPE},#{REMARK},#{RELEASE_DATE},#{SCRIPTSET}, sysdate) ;

	    end;

    	declare
	    	cnt number;
	    begin
	    	select count(*) into cnt from SSD_SM_FIRMWARE 
	    	where project = #{origindata.PROJECT}
	    	and firmware = #{origindata.FIRMWARE};

	    	update SSD_SM_FIRMWARE set
		    	MOD_DT = sysdate
		    	,NAND_TYPE = #{NAND_TYPE}
		    	,REMARK = #{REMARK}
		    	,RELEASE_DATE = to_date(#{RELEASE_DATE},'YYYY-MM-DD')
		    	,SCRIPTSET = #{SCRIPTSET}

	    	where project = #{origindata.PROJECT}
	    	and firmware = #{origindata.FIRMWARE};

	    end;

    	declare
	    	cnt number;
	    begin

	    	delete from SSD_SM_FIRMWARE 		 
	    	where project = #{origindata.PROJECT}
	    	and firmware = #{origindata.FIRMWARE};

	    end;

