mapper.select:
with tbl_a as ( SELECT a.SCRIPT_NAME, a.STAGE, b.SCRIPT_GROUP, b.GROUP_NAME, b.seq, a.CATEGORY, row_number() over (partition by b.SCRIPT_GROUP, a.category order by b.seq,a.CATEGORY,a.SCRIPT_NAME) row_num, a.SUB_CATEGORY, a.ITEM, a.SUB_ITEM, substr(a.DESCRIPTION,1,20) || ' ...' as DESCRIPTION_SIMPLE, a.DESCRIPTION, a.TESTER, a.INS_DT, a.MOD_DT from SSD_SM_MASTER a left outer join SSD_SM_MASTER_GROUP b on b.SCRIPT_GROUP = a.SCRIPT_GROUP where 1=1
and a.stage in
#{item}
and b.script_group in
#{item}
and a.category in
#{item}
and a.SUB_CATEGORY in
#{item}
and a.item in
#{item}
--order by category,test_item, script_name order by b.seq,category ,script_name )
select case when a.row_num > 0 and a.row_num <= 500 then '01. ~500' when a.row_num >500 and a.row_num <= 1000 then '02. 500~1000' when a.row_num > 1000 and a.row_num <= 1500 then '03. 1000~1500' when a.row_num > 1500 and a.row_num <= 2000 then '04. 1500~2000' when a.row_num > 2000 then '05. 2000~' end as rownum_grp ,a.* from tbl_a a

Select * From ( select A.*, ROWNUM AS ROW_NUM from ( SELECT a.SCRIPT_NAME, a.STAGE, b.SCRIPT_GROUP, b.GROUP_NAME, a.CATEGORY, a.SUB_CATEGORY, a.ITEM, a.SUB_ITEM, substr(a.DESCRIPTION,1,20) || ' ...' as DESCRIPTION_SIMPLE, a.DESCRIPTION, a.TESTER, a.INS_DT, a.MOD_DT from SSD_SM_MASTER a left outer join SSD_SM_MASTER_GROUP b on b.SCRIPT_GROUP = a.SCRIPT_GROUP where 1=1
and a.stage in
#{item}
and a.script_group in
#{item}
and a.category in
#{item}
and a.SUB_CATEGORY in
#{item}
and a.item in
#{item}
--order by category,test_item, script_name order by b.seq,a.category ,a.script_name ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
) A where
A.row_num > to_number(#{rows}) * (to_number(#{page}) -1) and A.row_num <= to_number(#{rows}) * to_number(#{page})

select ceil(count(*)/ #{rows} ) as total, #{page} as page , count(*) records from ( select a.* from ( SELECT a.SCRIPT_NAME, a.STAGE, b.SCRIPT_GROUP, b.GROUP_NAME, a.CATEGORY, a.SUB_CATEGORY, a.ITEM, a.SUB_ITEM, substr(a.DESCRIPTION,1,20) || ' ...' as DESCRIPTION_SIMPLE, a.DESCRIPTION, a.TESTER, a.INS_DT, a.MOD_DT from SSD_SM_MASTER a left outer join SSD_SM_MASTER_GROUP b on b.SCRIPT_GROUP = a.SCRIPT_GROUP where 1=1 and 2=2
and a.stage in
#{item}
and a.script_group in
#{item}
and a.category in
#{item}
and a.SUB_CATEGORY in
#{item}
and a.item in
#{item}
--order by category,test_item, script_name order by b.seq,a.category ,a.script_name ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
