)
) A

mapper.update:
    	declare
	    	cnt number;
	    	v_status varchar2(100);
	    begin

    		insert into SSD_SM_MASTER (SCRIPT_NAME, SCRIPT_GROUP, STAGE, CATEGORY, SUB_CATEGORY, ITEM, SUB_ITEM, INS_DT)
            values (#{SCRIPT_NAME}, #{SCRIPT_GROUP}, #{STAGE}, #{CATEGORY}, #{SUB_CATEGORY}, #{ITEM}, #{SUB_ITEM}, sysdate) ;

            select count(*) into cnt from SSD_SM_SCRIPTSET_MAP
            where SCRIPTSET = 'Normal'
            and SCRIPT_NAME = #{SCRIPT_NAME};

            if cnt = 0 then
            	insert into SSD_SM_SCRIPTSET_MAP (SCRIPTSET, SCRIPT_NAME,INS_DT)
            	values ('Normal', #{SCRIPT_NAME}, sysdate);
            end if;

	    end;

    	declare
	    	cnt number;
	    begin

	    		delete from SSD_SM_MASTER
	    		where 1=1
	    		and SCRIPT_NAME = #{detail.SCRIPT_NAME};

	    		--// 01. delete script set
	    		delete from SSD_SM_SCRIPTSET_MAP
	    		where 1=1
	    		and SCRIPT_NAME = #{detail.SCRIPT_NAME};

	    end;

    	declare
	    	cnt number;
	    	v_script_name varchar2(2000);
	    	v_convert_script varchar2(2000);
	    	v_status varchar2(10);
	    begin

	    		select count(*) into cnt from SSD_SM_MASTER
	    		where 1=1



			       			and ${item.field} = #{item.value}



			   			and 1=2


	    		;

	    		if cnt = 1 then
	    			update SSD_SM_MASTER set
	              	${fieldName} = #{fieldValue},	  
	              	MOD_DT = sysdate
	              	where 1=1



				       			and ${item.field} = #{item.value}



				   			and 1=2


	          		;



	          			v_script_name := #{fieldValue};   

	          			--// update SSD_SM_SCRIPTSET_MAP
	          			 update SSD_SM_SCRIPTSET_MAP set
	          			 SCRIPT_NAME = #{fieldValue},
	          			 MOD_DT = sysdate
	          			 where SCRIPT_NAME = #{fieldValueOrigin};

	          			--// update_log_table
	          			update SSD_SM_RESULT set
	          			SCRIPT_NAME = #{fieldValue},
	          			MOD_DT = sysdate
	          			where SCRIPT_NAME = #{fieldValueOrigin};







	        	end if;


	    end;

mapper.select:
Select * From ( select A.*, ROWNUM AS ROW_NUM from ( select a.SCRIPTSET, a.SCRIPT_NAME, b.STAGE, c.SCRIPT_GROUP, c.GROUP_NAME, c.SEQ, b.CATEGORY, b.SUB_CATEGORY, b.ITEM, b.SUB_ITEM, b.DESCRIPTION, b.TESTER, a.INS_DT, a.MOD_DT from SSD_SM_SCRIPTSET_MAP a left outer join SSD_SM_MASTER b on a.script_name = b.script_name left outer join SSD_SM_MASTER_GROUP c on c.SCRIPT_GROUP = b.SCRIPT_GROUP where 1=1
and a.SCRIPTSET = #{search_scriptset}
and b.stage in
#{item}
and c.script_group in
#{item}
and b.category in
#{item}
and b.SUB_CATEGORY in
#{item}
order by c.seq , b.CATEGORY, b.SUB_CATEGORY, b.ITEM, b.SUB_ITEM, b.SCRIPT_NAME ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
) A where
A.row_num > to_number(#{rows}) * (to_number(#{page}) -1) and A.row_num <= to_number(#{rows}) * to_number(#{page})

select ceil(count(*)/ #{rows} ) as total, #{page} as page , count(*) records from ( select a.* from ( select a.SCRIPTSET, a.SCRIPT_NAME, b.STAGE, c.SCRIPT_GROUP, c.GROUP_NAME, c.SEQ, b.CATEGORY, b.SUB_CATEGORY, b.ITEM, b.SUB_ITEM, b.DESCRIPTION, b.TESTER, a.INS_DT, a.MOD_DT from SSD_SM_SCRIPTSET_MAP a left outer join SSD_SM_MASTER b on a.script_name = b.script_name left outer join SSD_SM_MASTER_GROUP c on c.SCRIPT_GROUP = b.SCRIPT_GROUP where 1=1
and a.SCRIPTSET = #{search_scriptset}
and b.stage in
#{item}
and c.script_group in
#{item}
and b.category in
#{item}
and b.SUB_CATEGORY in
#{item}
order by c.seq, b.CATEGORY, b.SUB_CATEGORY, b.ITEM, b.SUB_ITEM, b.SCRIPT_NAME ) A where 1=1
and (
${filters.groupOp} REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
${filters.groupOp}
${group.groupOp} REGEXP_LIKE(${rule.field},#{rule.data},'i')
)
)

mapper.update:
    	declare
	    	cnt number;
	    begin
    		delete from SSD_SM_SCRIPTSET_MAP
    		where SCRIPTSET = #{origindata.SCRIPTSET} 
    		and SCRIPT_NAME = #{origindata.SCRIPT_NAME} 

	    end;

    	DECLARE
	    	cnt number;
	    	v_history_no varchar2(100) := #{history_no};
	    BEGIN
	    	INSERT INTO SSD_SM_HISTORY_NO (HISTORY_NO, INS_DT, TYPE, USER_COMMENT) 
	    	VALUES (v_history_no , sysdate,'COPY', 'scriptset_copy_from_master' );

	    	insert into SSD_SM_SCRIPTSET_MAP_H (HISTORY_NO, SCRIPTSET, SCRIPT_NAME, INS_DT, MOD_DT)
	    		select 
	    			v_history_no,
	    			SCRIPTSET,
	    			SCRIPT_NAME,
	    			INS_DT, 
	    			MOD_DT
	    		from SSD_SM_SCRIPTSET_MAP
	    		where 1=1
	    		and scriptset = #{searchJson.target.scriptset}
	    		 ;

	    End;

    	DECLARE
	    	cnt number;
	    	v_history_no varchar2(100) := #{history_no};
	    	v_status varchar2(10);
	    BEGIN

	    	--// Revision Start	    		
	    	For rec_firmware in (
	    		select distinct SCRIPTSET from SSD_SM_SCRIPTSET where SCRIPTSET = #{searchJson.target.scriptset} 
	    	) Loop

	    		--// second Loop
	    		FOR rec in (
	    			select * from SSD_SM_MASTER
					where 1=1
					and script_name = #{detail.SCRIPT_NAME}    			
	    		) LOOP 

					select count(*) into cnt from SSD_SM_SCRIPTSET_MAP
					where SCRIPTSET = rec_firmware.SCRIPTSET
					and script_name = rec.SCRIPT_NAME;

					IF cnt = 0 THEN
						insert into SSD_SM_SCRIPTSET_MAP (SCRIPTSET, SCRIPT_NAME, INS_DT)
						values (
							rec_firmware.SCRIPTSET 
							, rec.SCRIPT_NAME
							, sysdate
						);
					END IF;

				End Loop;

	    	End Loop;


	    END;

mapper.select:
