from SSD_SM_FIRMWARE f join SSD_SM_SCRIPTSET_MAP sm on sm.SCRIPTSET = f.SCRIPTSET join SSD_SM_MASTER m on m.script_name = sm.script_name left outer join SSD_SM_RESULT r on r.PROJECT = f.PROJECT and r.FIRMWARE = f.FIRMWARE and r.SCRIPT_NAME = sm.SCRIPT_NAME where 1=1 and f.firmware = #{search_firmware} and r.STATUS is null and r.STATUS = #{countGubun} and m.SCRIPT_GROUP = #{scriptGroup}
