aa.item_code, FN_SSD_TEST_GET_COMM_CD(category1,'01',4) as category1, FN_SSD_TEST_GET_COMM_CD(category2,'02',4) as category2, test_name, test_case_id, seq, decode((select max(to_number(rev)) from ssd_test_master b where aa.item_code = b.item_code), rev, rev||'*',rev) as rev, decode((select max(to_number(rev)) from ssd_test_master b where aa.item_code = b.item_code), rev, 'Y','N') as color_check, (select LISTAGG(rev, ',') WITHIN GROUP (ORDER BY rev) from ssd_test_master b where aa.item_code = b.item_code) AS rev_all, FN_SSD_TEST_GET_COMM_CD(test_type,'00',4) as test_type, FN_SSD_TEST_GET_COMM_CD(tool,'03',4) as tool, FN_SSD_TEST_GET_COMM_CD(protocol,'05',4) as protocol, sub_category1, sub_category2, command_line, arg_00, arg_02, arg_03, arg_04, arg_05, arg_06, arg_07, arg_08, arg_09, delete_yn, description, status, to_char(crt_dt,'YYYY-MM-DD') as crt_dt, crt_by, to_char(mod_dt,'YYYY-MM-DD') as mod_dt, mod_by, variation FROM ssd_test_master aa, ( SELECT distinct item_code FROM ssd_test_master a
and a.tool in
#{type}
and a.protocol in
#{type}
and a.category1 in
#{type}
and a.category2 in
#{type}
and a.item_code in
#{type}
) bb where aa.item_code = bb.item_code
and rev = (select max( to_number( cc.rev ) ) from ssd_test_master cc where aa.item_code = cc.item_code )
ORDER BY aa.item_code ASC, to_number(aa.rev) desc ) A )A where
