mapper.select:
	Select 
			*		
		From 
		(
			select
				A.*
			from 
			(


	select 
		SORUCE_ID,GEN_TYPE,DB_TYPE,URL,BTN_EXEC,SOURCE
	from GENSRC_LIST a 
	where 1=1
	
		and soruce_id = #{search_soruceId}
	
	
		and gen_type = #{search_genType}
	
	
		and db_type = #{search_dbType}
	
	
		and url = #{search_url}
	
	
		and btn_exec = #{search_btnExec}
	
	
		and test = #{search_test}
	






		                	${field.field} in 

			                	#{item}



			        		${field.field} = #{field.value}







		 order by ${sidx}  ${sord}  order by 1,2


	) A where 1=1 


					and (


			           		 ${filters.groupOp} 

								 is null
				    			REGEXP_LIKE(${item.field}, '^' || #{item.data} || '$','i')
				           		${item.field}  !=  #{item.data}
				           		${item.field}  <  #{item.data}
				           		${item.field}  <=  #{item.data}
				           		${item.field}  >  #{item.data}
				           		${item.field}  >=  #{item.data}
				           		REGEXP_LIKE(${item.field}, '^' || #{item.data},'i')
				           		 NOT REGEXP_LIKE(${item.field}, '^' || #{item.data},'i')


				           		 REGEXP_LIKE(${item.field}, #{item.data}|| '$','i')
				           		 NOT REGEXP_LIKE(${item.field}, #{item.data}|| '$','i')
				           		REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
				           		NOT REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')






			       			${filters.groupOp}

					        	 ${group.groupOp} 

									 is null	
					           		REGEXP_LIKE(${rule.field}, '^' || #{rule.data}|| '$','i')
					           		${rule.field}  !=  #{rule.data}
					           		${rule.field}  <  #{rule.data}
					           		${rule.field}  <=  #{rule.data}
					           		${rule.field}  >  #{rule.data}
					           		${rule.field}  >=  #{rule.data}
					           		REGEXP_LIKE(${rule.field}, '^' || #{rule.data},'i')
					           		 NOT REGEXP_LIKE(${rule.field}, '^' || #{rule.data},'i')


					           		 REGEXP_LIKE(${rule.field}, #{rule.data} || '$','i')
					           		 NOT REGEXP_LIKE(${rule.field}, #{rule.data} || '$','i')
					           		REGEXP_LIKE(${rule.field}, REGEXP_REPLACE(#{rule.data},' ','|'),'i')
					           		NOT REGEXP_LIKE(${rule.field}, REGEXP_REPLACE(#{rule.data},' ','|'),'i')





		       		 )


		) A
		LIMIT #{int_limit} OFFSET #{int_offset}

	select 
		ceil(count(*)/ #{rows} ) as TOTAL,
		#{page} as PAGE ,
		count(*) RECORDS 
	from 
	(
		select a.* from (


	select 
		SORUCE_ID,GEN_TYPE,DB_TYPE,URL,BTN_EXEC,SOURCE
	from GENSRC_LIST a 
	where 1=1
	
		and soruce_id = #{search_soruceId}
	
	
		and gen_type = #{search_genType}
	
	
		and db_type = #{search_dbType}
	
	
		and url = #{search_url}
	
	
		and btn_exec = #{search_btnExec}
	
	
		and test = #{search_test}
	






		                	${field.field} in 

			                	#{item}



			        		${field.field} = #{field.value}







		 order by ${sidx}  ${sord}  order by 1,2


	) a
	where 1=1 			


			and (


					 ${filters.groupOp} 

				    REGEXP_LIKE(${item.field}, '^' || #{item.data} || '$','i')
				           		${item.field}  !=  #{item.data}
				           		${item.field}  <  #{item.data}
				           		${item.field}  <=  #{item.data}
				           		${item.field}  >  #{item.data}
				           		${item.field}  >=  #{item.data}
				           		REGEXP_LIKE(${item.field}, '^' || #{item.data},'i')
				           		 NOT REGEXP_LIKE(${item.field}, '^' || #{item.data},'i')


				           		 REGEXP_LIKE(${item.field}, #{item.data} || '$','i')
				           		 NOT REGEXP_LIKE(${item.field}, #{item.data} || '$','i')
				           		REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')
				           		NOT REGEXP_LIKE(${item.field}, REGEXP_REPLACE(#{item.data},' ','|'),'i')






					${filters.groupOp}

						 ${group.groupOp} 

					           		REGEXP_LIKE(${rule.field}, '^' || #{rule.data} || '$','i')
					           		${rule.field}  !=  #{rule.data}
					           		${rule.field}  <  #{rule.data}
					           		${rule.field}  <=  #{rule.data}
					           		${rule.field}  >  #{rule.data}
					           		${rule.field}  >=  #{rule.data}
					           		REGEXP_LIKE(${rule.field}, '^' || #{rule.data},'i')
					           		 NOT REGEXP_LIKE(${rule.field}, '^' || #{rule.data},'i')


					           		 REGEXP_LIKE(${rule.field}, #{rule.data} || '$','i')
					           		 NOT REGEXP_LIKE(${rule.field}, #{rule.data} || '$','i')
					           		REGEXP_LIKE(${rule.field}, REGEXP_REPLACE(#{rule.data},' ','|'),'i')
					           		NOT REGEXP_LIKE(${rule.field}, REGEXP_REPLACE(#{rule.data},' ','|'),'i')





				)


	) a

select 
	SORUCE_ID,GEN_TYPE,DB_TYPE,URL,BTN_EXEC,SOURCE
from GENSRC_LIST a 
