mapper.select:
		SELECT DISTINCT SYEAR || '-' || SMONTH  TMONTH
     	FROM PDF_DATA_LIST
     	WHERE  SYEAR || SMONTH BETWEEN #{fromUseMonth} AND #{toUseMonth}

		SELECT SDATE USE_DATE_VAL
		FROM PDF_DATA_LIST		
		WHERE SITE_NM 		= #{siteNm}
        AND   LICENSE_NM  	= #{licenseNm}
		AND   SYEAR || SMONTH  = #{useMonth}

		SELECT DISTINCT SYEAR || '-' || SMONTH  TMONTH
     	FROM SOLUTIONPMS.PDF_DATA_LIST
     	WHERE  1=1
     	AND LICENSE_NM 	= #{licenseNm}   

         	and SITE_NM  in 

           		#{item}


     	AND SYEAR || SMONTH BETWEEN #{fromUseMonth} AND #{toUseMonth}
     	ORDER BY 1

SELECT VENDER, SITE_NM, SYEAR, SMONTH, MAX(DENIAL) DENIAL, MAX(PEAK) PEAK, MAX(USED) USED, MAX(AVAIL) AVAIL FROM SOLUTIONPMS.PDF_DATA_LIST WHERE 1=1 AND LICENSE_NM = #{licenseNm}
and SITE_NM in
#{item}
AND SYEAR || SMONTH BETWEEN #{fromUseMonth} AND #{toUseMonth} GROUP BY VENDER,SITE_NM,SYEAR,SMONTH
